import React from 'react';
import {Line} from 'react-chartjs-2';

class Graph extends React.Component {
	constructor(props) {
		super(props)
		this.state = {
			datasets: [],
			labels: []
		}
	}

	static colors = [
		'rgb(76, 128, 24, 1)',
		'rgb(24, 76, 128, 1)',
		'rgb(128, 24, 128, 1)',
		'rgb(216, 12, 12, 1)',
		'rgb(225, 122, 19, 1)',
		'rgb(19, 225, 225, 1)',
		'rgb(68, 97, 39, 1)',
		'rgb(97, 39, 39, 1)'
	]

	generate_color() {
		var o = Math.round, r = Math.random, s = 255;
		return 'rgba(' + o(r()*s) + ',' + o(r()*s) + ',' + o(r()*s) + ',' + 1 + ')';
	}

	create_dataset(ranks, player_name) {
		var color;
		if (this.state.datasets.length < 9) {
			console.log("hjey")
			color = Graph.colors[this.state.length - 1]
			console.log(color)
		}
		else{
			color = this.generate_color()
		}
		return {
			label: player_name,
			fill: false,
			lineTension: 0.1,
			backgroundColor: 'rgba(75,192,192,0.4)',
			borderColor: color,
			borderCapStyle: 'butt',
			borderDash: [],
			borderDashOffset: 0.0,
			borderJoinStyle: 'miter',
			pointBorderColor: 'rgba(75,192,192,1)',
			pointBackgroundColor: '#fff',
			pointBorderWidth: 1,
			pointHoverRadius: 5,
			pointHoverBackgroundColor: 'rgba(75,192,192,1)',
			pointHoverBorderColor: 'rgba(220,220,220,1)',
			pointHoverBorderWidth: 2,
			pointRadius: 1,
			pointHitRadius: 10,
			data: ranks
		}
	}

	addPlayer(player_id, player_name) {

		fetch(`/get_ranking_history?player_id=${player_id}&starting_age=25&ending_age=26`).then(response =>
			response.json().then(data => {
				var ranks = data['data'].map(x => x['rank'])
				this.setState({
					labels: data['data'].map(x => x['age']),
					datasets: [...this.state.datasets, this.create_dataset(ranks, player_name)]
				})
			}))
	}

	render() {

		const data = {
			labels: this.state.labels,
			datasets: this.state.datasets
		};



		return (
			<div>
				<Line data={data} />
			</div>
		)
	}
}

export default Graph;
