			pointHoverBorderColor: 'rgba(220,220,220,1)',
			pointHoverBorderWidth: 2,
			pointRadius: 1,
			pointHitRadius: 10,
			data: ranks
		}
	}

	addPlayer(player_id) {

		fetch(`/get_ranking_history?player_id=${player_id}&starting_age=25&ending_age=26`).then(response =>
			response.json().then(data => {
				var ranks = data.map(x => x['rank'])
				this.setState({
					labels: data.map(x => x['age']),
					data: [...this.state.datasets, this.create_dataset(ranks, player_id)]
