import React from 'react';
import {Line} from 'react-chartjs-2';

class Graph extends React.Component {
	constructor(props) {
		super(props)
		this.state = {

			ranking_histories: []
		}
	}

	addPlayer(player_id) {

		fetch(`/get_ranking_history?player_id=${player_id}&starting_age=25&ending_age=26`).then(response =>
			response.json().then(data => {
				var new_entry = {id: player_id, data: data}
				this.setState({
					ranking_histories: [...this.state.ranking_histories, new_entry]
				})
			}))
	}

	render() {
		var players = this.state.ranking_histories.map(x => {
			return <li key={x['id']}>{x['id']}</li>
		})

		const data = {
  labels: ['January', 'February', 'March', 'April', 'May', 'June', 'July'],
  datasets: [
    {
      label: 'My First dataset',
      fill: false,
      lineTension: 0.1,
      backgroundColor: 'rgba(75,192,192,0.4)',
      borderColor: 'rgba(75,192,192,1)',
      borderCapStyle: 'butt',
      borderDash: [],
      borderDashOffset: 0.0,
      borderJoinStyle: 'miter',
      pointBorderColor: 'rgba(75,192,192,1)',
      pointBackgroundColor: '#fff',
      pointBorderWidth: 1,
      pointHoverRadius: 5,
      pointHoverBackgroundColor: 'rgba(75,192,192,1)',
      pointHoverBorderColor: 'rgba(220,220,220,1)',
      pointHoverBorderWidth: 2,
      pointRadius: 1,
      pointHitRadius: 10,
      data: [[65, 59, 80, 81, 56, 55, 40], [45, 89, 23, 40, 65, 35, 71]]
    }
  ]
};



		return (
			<div>
				<Line data={data} />
			</div>
		)
	}
}

export default Graph;
