import React, {useEffect, useState} from 'react';
import { Dropdown } from 'semantic-ui-react'
import faker from 'faker'

class PlayerSelector extends React.Component {
	constructor(props) {
		super(props)
		this.state = {
			players: []
		}
	}

	componentDidMount() {

		fetch("/topTenPlayers").then(response => 
			response.json().then(data => {
				// console.log(data['data'])

				const arr = []
				for (var i = 0; i < data['data'].length; i++) {
					d = data['data']
					arr.push({text: d['last_name'], value: d['id'], key: d['id']})
				}



				this.setState({
					players: data['data'].map(x => (x) => {
						return {text: x['last_name'], value: x['id'], key:x['id']}
					})
				})
		}))

		// this.setState({
		// 	players: [{key:'A', text:'A', 'value':'A'}]
		// })
	}



	search_change(data) {
		console.log(data)
	}

	label_click(data) {
		console.log(data)
	}

	render() {
		const friendOptions = [
		  {
		    key: 'Jenny Hess',
		    text: 'Jenny Hess',
		    value: 'Jenny Hess',
		  },
		  {
		    key: 'Elliot Fu',
		    text: 'Elliot Fu',
		    value: 'Elliot Fu',
		  }
		]
		return (
			<Dropdown
			    placeholder='Select a Player'
			    search
			    selection
			    onChange={(_, data) => this.label_click(data)}
			    onSearchChange={(_, data) => this.search_change(data)}
			    options={this.state.players}
			/>
		)
	}
}

export default PlayerSelector;
