import React, {useEffect, useState} from 'react';
import { Dropdown } from 'semantic-ui-react'
import faker from 'faker'

class PlayerSelector extends React.Component {
	constructor(props) {
		super(props)
		this.state = {
			players: []
		}
	}

	convert_data(data) {
		const arr = []
		for (var i = 0; i < data.length; i++) {
			let d = data[i]
			arr.push({text: d['last_name'].concat(", ", d['first_name']), value: d['id'], key: d['id']})
		}
		return arr
	}

	componentDidMount() {

		fetch("/topTenPlayers").then(response => 
			response.json().then(data => {
				this.setState({
					players: this.convert_data(data['data'])
				})
		}))

		// this.setState({
		// 	players: [{key:'A', text:'A', 'value':'A'}]
		// })
	}



	search_change(data) {
		console.log(data)
	}

	label_click(data) {
		console.log(data)
	}

	render() {
		const friendOptions = [
		  {
		    key: 'Jenny Hess',
		    text: 'Jenny Hess',
		    value: 'Jenny Hess',
		  },
		  {
		    key: 'Elliot Fu',
		    text: 'Elliot Fu',
		    value: 'Elliot Fu',
		  }
		]
		return (
			<Dropdown
			    placeholder='Select a Player'
			    search
			    selection
			    onChange={(_, data) => this.label_click(data)}
			    onSearchChange={(_, data) => this.search_change(data)}
			    options={this.state.players}
			/>
		)
	}
}

export default PlayerSelector;
